// internal function to find the correct place for a node in a tree
func insertNode(node, newNode *Node) {
 if newNode.key < node.key {
  if node.left == nil {
   node.left = newNode
  } else {
   insertNode(node.left, newNode)
  }
 } else {
  if node.right == nil {
   node.right = newNode
  } else {
   insertNode(node.right, newNode)
  }
 }
}

